    def update_realsense_frame(self):
        img_color_source, img_color, img_infr1, img_infr2, point2D, point3D = self.camera_cap.detect_infrared_ball()
        frame_color = cv2.resize(img_color, (320, 240))
        frame_infr1 = cv2.resize(img_infr1, (320, 240))
        frame_infr2 = cv2.resize(img_infr2, (320, 240))

        frame_color = cv2.cvtColor(frame_color, cv2.COLOR_BGR2RGB)
        frame_infr1 = cv2.cvtColor(frame_infr1, cv2.COLOR_BGR2RGB)
        frame_infr2 = cv2.cvtColor(frame_infr2, cv2.COLOR_BGR2RGB)

        showImage_color = QImage(frame_color.data, frame_color.shape[1], frame_color.shape[0], QImage.Format_RGB888)
        showImage_infr1 = QImage(frame_infr1.data, frame_infr1.shape[1], frame_infr1.shape[0], QImage.Format_RGB888)
        showImage_infr2 = QImage(frame_infr2.data, frame_infr2.shape[1], frame_infr2.shape[0], QImage.Format_RGB888)
        self.label_7.setPixmap(QPixmap.fromImage(showImage_infr1))
        self.label_8.setPixmap(QPixmap.fromImage(showImage_color))
        self.label_9.setPixmap(QPixmap.fromImage(showImage_infr2))

        self.image_color = img_color_source
        self.point2D = point2D
        self.point3D = point3D
        self.pointNum = len(point2D)

        self.infrared_ball_display()

        if self.out is None:
            pass
        else:
            if self.record_ball_number != 0 and self.record_ball_number == self.pointNum:
                self.out.write(self.image_color)
                self.Sequence3D.append(self.point3D)
                self.Sequence2D.append(self.point2D)

